<?	if(permissaoSubOperacao($localModulo,$localOperacao,"I") == false){		$local_Erro = 2;	} else{		$sql2	=	"select (max(IdCartao)+1) IdCartao from PessoaCartao Where IdPessoa = '$local_IdPessoa'";		$res2	=	mysql_query($sql2,$con);		$lin2	=	@mysql_fetch_array($res2);				if($lin2[IdCartao] != NULL){			$local_IdCartao = $lin2[IdCartao];		} else{			$local_IdCartao = 1;		}				$validade = $local_MesValidade."/".$local_AnoValidade;					$sql = "INSERT INTO PessoaCartao SET 					IdLoja					='$local_IdLoja',					IdPessoa				='$local_IdPessoa',					IdCartao				='$local_IdCartao',					IdBandeira				='$local_IdBandeiraCartao',					NomeTitular				='$local_NomeTitular',					NumeroCartao			='$local_NumeroCartao',					Validade				='$validade',					CodigoSeguranca			='$local_CodigoSeguranca',					DiaVencimentoFatura		='$local_DiaVencimentoFatura',					IdStatus				='$local_IdStatus',					LoginCriacao			='$local_Login', 					DataCriacao				=(concat(curdate(),' ',curtime()));";		if(mysql_query($sql, $con) == true){			// Muda a aчуo para Inserir			$local_Acao = 'alterar';			$local_Erro = 3;			// Mensagem de Inserчуo Positiva		} else{			// Muda a aчуo para Inserir			$local_Acao = 'inserir';			$local_Erro = 1;			// Mensagem de Inserчуo Negativa		}	}?>