<?	if(permissaoSubOperacao($localModulo,$localOperacao,"I") == false){		$local_Erro = 2;	} else{		$sql2	=	"select (max(IdContaDebito)+1) IdContaDebito from PessoaContaDebito where IdPessoa = $local_IdPessoa";		$res2	=	mysql_query($sql2,$con);		$lin2	=	@mysql_fetch_array($res2);				if($lin2[IdContaDebito] != NULL){			$local_IdContaDebito = $lin2[IdContaDebito];		} else{			$local_IdContaDebito = 1;		}					$sql = "INSERT INTO PessoaContaDebito SET 					IdLoja					='$local_IdLoja',					IdPessoa				='$local_IdPessoa',					IdContaDebito			='$local_IdContaDebito',					DescricaoContaDebito	='$local_DescricaoContaDebito',					IdLocalCobranca			='$local_IdLocalCobranca',					NumeroAgencia			='$local_NumeroAgencia',					DigitoAgencia			='$local_DigitoAgencia',					NumeroConta				='$local_NumeroConta',					DigitoConta				='$local_DigitoConta',					IdStatus				='$local_IdStatus',					LoginCriacao			='$local_Login', 					DataCriacao				=(concat(curdate(),' ',curtime()));";		if(mysql_query($sql, $con) == true){			// Muda a ação para Inserir			$local_Acao = 'alterar';			$local_Erro = 3;			// Mensagem de Inserção Positiva		} else{			// Muda a ação para Inserir			$local_Acao = 'inserir';			$local_Erro = 1;			// Mensagem de Inserção Negativa		}	}?>